"use strict";var timer,points=0,gameInProgress=!1;function startGame(){gameInProgress?(resetGame(),document.getElementById("startBtn").textContent="Iniciar"):(updatePoints(),startTimer(45),gameInProgress=!0,document.getElementById("startBtn").textContent="Reiniciar")}function addPoint(){gameInProgress&&(points++,updatePoints())}function storeClick(){gameInProgress&&(points*=2,updatePoints())}function updatePoints(){document.getElementById("points").innerText="".concat(points)}function startTimer(duration){var timerDisplay=document.getElementById("countdown");timer=setInterval(function(){duration<0?(clearInterval(timer),showResult()):(timerDisplay.textContent=duration,duration--)},1e3)}function showResult(){window.location.href="result.html?score=".concat(points),gameInProgress=!1}function resetTimer(){clearInterval(timer),document.getElementById("countdown").textContent="45",gameInProgress=!1,document.getElementById("resetBtn").style.display="none",document.getElementById("startBtn").style.display="block"}function resetGame(){clearInterval(timer),document.getElementById("countdown").textContent="45",document.getElementById("points").textContent="0",gameInProgress=!1,points=0}